import React, { Fragment, Component } from 'react'
import { Link } from 'react-router-dom'
import { Spinner } from 'react-bootstrap';

class User extends Component {
    componentDidMount() {
        this.props.getUser(this.props.match.params.login)
    }
    render() {
        const {
            name,
            avatar_url,
            location,
            bio,
            blog,
            login,
            html_ulr,
            company,
            followers,
            following,
            public_repos,
            public_gists,
            hireable
        } = this.props.user;


        const { loading } = this.props

        if (loading) return <Spinner />

        return (
            <Fragment>
                <Link to='/' className='btn btn-light'>
                    Back
                </Link>
                Hireable: {' '}
                {hireable ? (
                    <i className="fa fa-check text-success" />
                ) : (
                        < i classNames="fa fa-times-circle text-danger" />)}
                <div className="card grid-2">
                    <div className="all-center">
                        <img className="round-img" src={avatar_url} alt=""
                            style={{ width: '150px' }} />
                        <h1>{name}</h1>
                        <p>Location:{location}</p>
                    </div>
                    <div>
                        {bio && (<Fragment>
                            <h3>Bio</h3>
                            <p>{bio}</p>
                        </Fragment>)}
                        <a href={html_ulr} className='btn btn-dark my-1'>
                            Visit Github Profile
                        </a>
                        <ul>
                            <li>
                                {login && <Fragment>
                                    <strong>Username:</strong> {login}
                                </Fragment>}
                            </li>
                            <li>
                                {company && <Fragment>
                                    <strong>Company:</strong> {login}
                                </Fragment>}
                            </li>
                            <li>
                                {blog && <Fragment>
                                    <strong>Website:</strong> {login}
                                </Fragment>}
                            </li>
                        </ul>
                    </div>
                </div>
            </Fragment>
        )
    }
}

export default User;
