import React from 'react'
import User from './Users'
import PropTypes from 'prop-types'


const Useritem = ({ users, loading }) => {

        return (
            <div style={userStyle}>
                {users.map(user => (
                    <User key={user.id} user={user} />
                ))}
            </div>
        )
}

